document.addEventListener('DOMContentLoaded', function(){
  const container = document.getElementById('three-canvas');
  if(!container) return;
  const width = container.clientWidth || 400;
  const height = 300;
  const scene = new THREE.Scene();
  const camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 1000);
  const renderer = new THREE.WebGLRenderer({alpha:true, antialias:true});
  renderer.setSize(width, height);
  container.appendChild(renderer.domElement);
  const geometry = new THREE.TorusKnotGeometry(10,3,200,32);
  const material = new THREE.MeshStandardMaterial({color:0xff6a00, metalness:0.5, roughness:0.2});
  const mesh = new THREE.Mesh(geometry, material);
  scene.add(mesh);
  const light = new THREE.PointLight(0xffffff,1);
  light.position.set(50,50,50); scene.add(light);
  camera.position.z = 50;
  function animate(){ requestAnimationFrame(animate); mesh.rotation.x += 0.01; mesh.rotation.y += 0.008; renderer.render(scene, camera); }
  animate();
});